INSERT INTO user_status (status_name, description) VALUES ('Täisliige', 'Noorliikmel on õigus kandideerida täisliikmeks, kui ta on olnud vähemalt kolmel projektil abiks või olnud ühe projekti projektijuht. Kandideerimiseks on vaja esitada motivatsioonikiri koos avaldusega juhatusele, kes kinnitab või lükkab tagasi avalduse.');
INSERT INTO user_status (status_name, description) VALUES ('Noorliige', 'Uusliikmest saab noorliige, kui ta on ühel projektil olnud abis, käinud kohal ühel üldkoosolekul ja tema sobivuse on kinnitanud uusliikme mentor.');
INSERT INTO user_status (status_name, description) VALUES ('Uusliige', 'Uusliikme roll omastatakse IT-teaduskonna tudengile vahetult pärast liikmeavalduse vastuvõttu. Uusliige on esimene samm ITÜKi täisliikmeks saamisel. Uusliige valib endale mentori, kes on abiks üliõpilaskogusse sisseelamisel.');
INSERT INTO user_status (status_name, description) VALUES ('Vilistlane', 'Vilistlaseks saab endine ITÜKi liige, kui ta on olnud vähemalt 2 semestrit ITÜKis noor- ja/või täisliige. Vilistlane ei pea olema TalTechi IT-teaduskonna tudeng.');
INSERT INTO user_status (status_name, description) VALUES ('Juhatus', 'Juhatuse liikmeks saab kandideerida täisliige või noorliige. Juhatuse liikme kinnitavad täisliikmed üldkoosolekul.');
